import React, { useState } from 'react';

const ChatInterface = () => {
  const [query, setQuery] = useState("");
  const [mode, setMode] = useState("rag");
  const [messages, setMessages] = useState([]);

  const sendMessage = async () => {
    const response = await fetch("http://localhost:8000/api/chat/", {
      method: "POST",
      headers: {"Content-Type": "application/json"},
      body: JSON.stringify({ query, mode })
    });
    const data = await response.json();
    setMessages([...messages, { user: query, bot: data.answer }]);
    setQuery("");
  };

  return (
    <div className="chat-container">
      <h2>Chat</h2>
      <div className="messages">
        {messages.map((m,i) => (
          <div key={i} className="message-pair">
            <div className="user-message"><strong>Tú:</strong> {m.user}</div>
            <div className="bot-message"><strong>Bot:</strong> {m.bot}</div>
          </div>
        ))}
      </div>
      <div className="controls">
        <select value={mode} onChange={(e) => setMode(e.target.value)}>
          <option value="rag">RAG</option>
          <option value="finetune">Fine-Tune (Llama2)</option>
          <option value="hybrid">Híbrido</option>
        </select>
        <input value={query} onChange={(e) => setQuery(e.target.value)} placeholder="Escribe tu pregunta..." />
        <button onClick={sendMessage}>Enviar</button>
      </div>
    </div>
  );
};

export default ChatInterface;
